mapper.select:


	select 
        rno, bno, id, rcontent, score, replydate
		  from 
		      (
		      select /*+INDEX_DESC(review idx_review) */
		        rownum rn, rno, bno, id, rcontent, score, replydate
		      from review
		      where bno = #{bno}
		      and rno > 0
		      and rownum <= #{cri.pageNum} * #{cri.amount}
              )
              where rn > (#{cri.pageNum} -1) * #{cri.amount}
